      try {
        await fs.writeFile(filePath, content, {
          flag: "wx",
        })
        console.log("Succesfully written " + filePath)
        console.log(localPath)
      } catch (error) {
        console.log(error)
        console.log("file " + filePath + " already exists")
      }
    })()
  })

cli.parse()
